import numpy as np; from scipy.io import wavfile; sample_rate = 44100; t = np.linspace(0, 0.3, int(0.3*sample_rate), endpoint=False); beep1 = 0.5*np.sin(2*np.pi*1000*t[0:int(0.1*sample_rate)]); silence = np.zeros(int(0.1*sample_rate)); beep2 = 0.5*np.sin(2*np.pi*1200*t[0:int(0.1*sample_rate)]); audio = np.hstack((beep1, silence, beep2)); wavfile.write("sound_feedback.wav", sample_rate, audio.astype(np.float32)); print("Sound file created successfully")
